CREATE TABLE "mytable" (
    "name" text,
    "continent" text,
    "area" bigint,
    "population" bigint,
    "gdp" bigint,
    "capital" text,
    "tld" text,
    "flag" text
  );
  
  
SELECT *
FROM world AS x
WHERE area >= ALL (
SELECT area
FROM world y
WHERE y.continent = x.continent
AND area>0);

SELECT continent, min(name) AS small
FROM world
GROUP BY continent;

SELECT DISTINCT continent 
FROM world;

SELECT name, continent, population
FROM world
WHERE continent IN (SELECT continent
FROM world
GROUP BY continent
HAVING max(population)<= 25000000 );

SELECT continent
FROM world
GROUP BY continent
HAVING max(population)<= 25000000;
